witches_yavinScreenplay = ScreenPlay:new {
	numberOfActs = 1,

	spawnPoints = {
		{x= 1,y= 1,radius= 4000 },
		{x= -2500,y= 2500,radius= 2000},
		{x= 2500,y= 2500,radius= 2000},
		{x= 2500,y= -2500,radius= 2000},
		{x= -2500,y= -2500,radius= 2000},
		{x= 10,y= -3000,radius= 1500},
		{x= 10,y= 3000,radius= 1500}
	}
}
registerScreenPlay("witches_yavinScreenplay", true)

function witches_yavinScreenplay:start()
	if (isZoneEnabled("yavin4")) then
		self:spawnMobiles(self.spawnPoints)
	end
end

